	$('#top-link').click(function(){
		window.backPlace = window.backPlace || 0;
		if (window.backPlace > 0 && $(window).scrollTop() < 200) {
			$('html, body').animate({scrollTop: window.backPlace}, 500);
			$(this).removeClass("backPlace");
		} else if ($(window).scrollTop() > 199) {
			window.backPlace = $(window).scrollTop();
			$(this).addClass("backPlace");
			$('html, body').animate({scrollTop: 0}, 250);
		}
		return false;
	});

	$(window).scroll(function() {
		if ($(window).scrollTop() > 0 && !$('#top-link').hasClass("visible") && !$('#top-link').hasClass("backPlace")) {
			$('#top-link').animate({"opacity":1},1000).addClass("visible");
		} 
		if (!$(window).scrollTop() && $('#top-link').hasClass("visible") && !$('#top-link').hasClass("backPlace")) {
			$('#top-link').animate({"opacity":0},500).removeClass("visible");
		}
	});

});
